mapper.select:
select * from
( select A.*, rownum r from
( select * from cafe_stock order by stock_code desc) A
)
where r >= #{ start } and r <= #{ end }

mapper.update:
update cafe_stock set stock_productname = #{stock_productname}, stock_detailname = #{stock_detailname},
stock_standard = #{stock_standard}, stock_price = #{stock_price}, stock_image = #{stock_image}, 
delivery_number = #{delivery_number}
where stock_code = #{stock_code}
